
var SAdataTable = function($scope, $) {
    
    var $_this = $scope.find(".sa-el-data-table-wrap"),
        $id = $_this.data("table_id");


  var $enable_table = $_this.data('table_enabled'),
        $id           = $_this.data('table_id');

    if( true == $enable_table ) $("#sa-el-data-table-"+$id).tablesorter();
    if( $enable_table != true ) {
        $('table#sa-el-data-table-'+$id+' .sorting').addClass('sorting-none');
        $('table#sa-el-data-table-'+$id+' .sorting_desc').addClass('sorting-none');
        $('table#sa-el-data-table-'+$id+' .sorting_asc').addClass('sorting-none');
    }

    var responsive = $_this.data("custom_responsive");
    if (true == responsive) {
        var $th = $scope.find(".sa-el-data-table").find("th");
        var $tbody = $scope.find(".sa-el-data-table").find("tbody");

        $tbody.find("tr").each(function(i, item) {
            $(item)
                .find("td .td-content-wrapper")
                .each(function(index, item) {
                    $(this).prepend(
                        '<div class="th-mobile-screen">' +
                            $th.eq(index).html() +
                            "</div>"
                    );
                });
        });
    
    }
};
jQuery(window).on("elementor/frontend/init", function() {

    elementorFrontend.hooks.addAction(
        "frontend/element_ready/sa_el_data_table.default",
        SAdataTable
    );
});