var SaElFancyText = function($scope, $) {
    var $safancyText = $scope.find(".sa_el_fancy_text_container").eq(0),
        $id =
        $safancyText.data("fancy-text-id") !== undefined ?
        $safancyText.data("fancy-text-id") :
        "",
        $fancy_text =
        $safancyText.data("fancy-text") !== undefined ?
        $safancyText.data("fancy-text") :
        "",
        $transition_type =
        $safancyText.data("fancy-text-transition-type") !== undefined ?
        $safancyText.data("fancy-text-transition-type") :
        "",
        $fancy_text_speed =
        $safancyText.data("fancy-text-speed") !== undefined ?
        $safancyText.data("fancy-text-speed") :
        "",
        $fancy_text_delay =
        $safancyText.data("fancy-text-delay") !== undefined ?
        $safancyText.data("fancy-text-delay") :
        "",
        $fancy_text_cursor =
        $safancyText.data("fancy-text-cursor") !== undefined ? true : false,
        $fancy_text_loop =
        $safancyText.data("fancy-text-loop") !== undefined ?
        $safancyText.data("fancy-text-loop") == "yes" ?
        true :
        false :
        false;
    $fancy_text = $fancy_text.split("|");

    if ($transition_type == "typing") {
        $("#sa_el_fancy_text_" + $id).typed({
            strings: $fancy_text,
            typeSpeed: $fancy_text_speed,
            backSpeed: 0,
            startDelay: 300,
            backDelay: $fancy_text_delay,
            showCursor: $fancy_text_cursor,
            loop: $fancy_text_loop
        });
    }

    if ($transition_type != "typing") {
        $("#sa_el_fancy_text_" + $id).Morphext({
            animation: $transition_type,
            separator: ", ",
            speed: $fancy_text_delay,
            complete: function() {
                /* Overrides default empty function! */
            }
        });
    }
};
jQuery(window).on("elementor/frontend/init", function() {
    elementorFrontend.hooks.addAction(
        "frontend/element_ready/sa_el_fancy_text.default",
        SaElFancyText
    );
});