var SAImageAccordion = function($scope, $) {
    var $sa_imageAccordion = $scope.find(".sa-el-img-accordion").eq(0),
        $id =
            $sa_imageAccordion.data("img-accordion-id") !== undefined
                ? $sa_imageAccordion.data("img-accordion-id")
                : "",
        $type =
            $sa_imageAccordion.data("img-accordion-type") !== undefined
                ? $sa_imageAccordion.data("img-accordion-type")
                : "";

    if ("on-click" === $type) {
        $("#sa-el-img-accordion-" + $id + " a").on("click", function(e) {
            if ($(this).hasClass("overlay-active") == false) {
                e.preventDefault();
            }

            $("#sa-el-img-accordion-" + $id + " a").css("flex", "1");
            $(this)
                .find(".overlay")
                .parent("a")
                .addClass("overlay-active");
            $("#sa-el-img-accordion-" + $id + " a")
                .find(".overlay-inner")
                .removeClass("overlay-inner-show");
            $(this)
                .find(".overlay-inner")
                .addClass("overlay-inner-show");
            $(this).css("flex", "3");
        });
        $("#sa-el-img-accordion-" + $id + " a").on("blur", function(e) {
            $("#sa-el-img-accordion-" + $id + " a").css("flex", "1");
            $("#sa-el-img-accordion-" + $id + " a")
                .find(".overlay-inner")
                .removeClass("overlay-inner-show");
            $(this)
                .find(".overlay")
                .parent("a")
                .removeClass("overlay-active");
        });
    }
};
jQuery(window).on("elementor/frontend/init", function() {
    elementorFrontend.hooks.addAction(
        "frontend/element_ready/sa_el_img_accordion.default",
        SAImageAccordion
    );
});
