/*=================================
 Interactive Card
=================================*/
var SAInteractiveCard = function($scope, $) {
	var $SAinteractiveCard = $scope.find(".interactive-card").eq(0),
		$id =
			$SAinteractiveCard.data("interactive-card-id") !== undefined
				? $SAinteractiveCard.data("interactive-card-id")
				: "",
		$animation =
			$SAinteractiveCard.data("animation") !== undefined
				? $SAinteractiveCard.data("animation")
				: "",
		$animation_time =
			$SAinteractiveCard.data("animation-time") !== undefined
				? $SAinteractiveCard.data("animation-time")
				: "";

	var options = {
		containerId: "interactive-card-" + $id,
		frontAnimation: {
			start: "fade-out",
			end: "fade-in"
		},
		rearAnimation: {
			start: "zoom-out",
			end: "zoom-in"
		},
		contentAnimation: $animation.toString(),
		revealTime: $animation_time
	};
	interactiveCards(options);
};
jQuery(window).on("elementor/frontend/init", function() {
	elementorFrontend.hooks.addAction(
		"frontend/element_ready/sa_el_interactive_card.default",
		SAInteractiveCard
	);
});
